import { Component } from '@angular/core';
import { Todo } from '../../shared/models/todo.model';
import { TodoService } from '../../shared/services/todo.service';

@Component({
  selector: 'app-new-task',
  templateUrl: './new-task.component.html',
  styleUrls: ['./new-task.component.css']
})
export class NewTaskComponent {
  newTaskTitle: string = '';

  constructor(private todoService: TodoService) { }

  addTask() {
  if (!this.newTaskTitle.trim()) return;

  const newTodo: Todo = {
    id: this.todoService.getTodoNewId(),
    title: this.newTaskTitle,
    completed: false
  };

  this.todoService.addTodo(newTodo);
  this.newTaskTitle = ''; // limpa o campo
}
}
<div class="new-task">
  <div class="new-task_input-container">
    <label class="new-task_label" for="new-task">Título da Tarefa</label>
    <input
      id="new-task"
      class="new-task_input"
      type="text"
      [(ngModel)]="newTaskTitle"
      placeholder="Descreva aqui de forma breve a sua tarefa."
    />
  </div>
  <button class="new-task_button" (click)="addTask()">
    Salvar
  </button>
</div>
