import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Todo } from '../../shared/models/todo.model';
import { TodoService } from '../../shared/services/todo.service';

@Component({
  selector: 'app-todo-item',
  templateUrl: './todo-item.component.html',
  styleUrls: ['./todo-item.component.css']
})
export class TodoItemComponent {
  @Input() todo!: Todo;

  @Output() deletedTodo: EventEmitter<number> = new EventEmitter<number>();

  @Output() editTodo: EventEmitter<Todo> = new EventEmitter<Todo>();

  constructor(private todoService: TodoService) {}

  deleteTodo(): void {
    if (confirm('Are you sure you want to delete this task?')) {
      this.todoService.deleteTodo(this.todo.id);
      this.deletedTodo.emit(this.todo.id);
    }
  }

  onTaskChecked(): void {
    this.todoService.updateTodo(this.todo);
  }

  editar() {
    this.editTodo.emit(this.todo);
  }
}


<div class="todo-item">
  <div class="todo-item_content">
    <input
      id="task-{{ todo.id }}"
      type="checkbox"
      [(ngModel)]="todo.completed"
      (change)="onTaskChecked()"
    />
    <label
      for="task-{{ todo.id }}"
      [ngClass]="{ 'completed': todo.completed, 'incomplete': !todo.completed }"
    >{{ todo.title }}</label>
  </div>

  <button class="todo-item_edit" (click)="editar()">
    <i class="fas fa-edit fa-lg"></i> Editar
  </button>

  <button class="todo-item_delete" style="color: rgb(237, 1, 1)" (click)="deleteTodo()">
    <i class="fas fa-trash-alt fa-lg"></i> Remover
  </button>
</div>
