import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeadeComponent implements OnInit {

  constructor() { }

  ngOnInit(): void {
  }

}
<header class="header">
  <img class="header-logo" src="https://sig.imts.com.br/assets/img/logo-imts-horizontal.png" alt="Logo">
  <div class="header-title">
    <h1>Lista de Tarefas</h1>
    <p>O caminho mais simples para organizar suas tarefas e fazer sua vida mais fácil.</p>
  </div>
</header>
